function gocvecto(a, b, c, d, e, f) {
    const dot_product = a * d + b * e + c * f;
    const norm_vector1 = Math.sqrt(a ** 2 + b ** 2 + c ** 2);
    const norm_vector2 = Math.sqrt(d ** 2 + e ** 2 + f ** 2);
    const cos_theta = dot_product / (norm_vector1 * norm_vector2);
    return cos_theta;
}

